package 실습04;

public class Equation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub





		for ( int y = 1; y <= 60; y++) {  //y value
			for(int x= 1; x <= 60; x++) {
				if ((4*x) +(5*y) == 60) {
					System.out.println(x +","+ y); //x value
				}
				
			}

		}
	}

}
