package hello;

public class ComparisonisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		int x = 3;
		int y = 4;
		
		System.out.println((x==y)+"");
		System.out.println((x!=y)+"");
		System.out.println((x>y)+"");
		System.out.println((x<y)+"");
		System.out.println((x<=y)+"");
		
	}

}
